import React, { Component } from "react";
import * as BooksAPI from "../BooksAPI";
import BookHolder from "./BookHolder";
export default class SearchPage extends Component {
  state = { query: "" };



  render() {
    console.log(this.state.booksB);

    return (
      <div className="search-books">
        <div className="search-books-bar">
          <button
            className="close-search"
            onClick={() => this.setState({ booksB: [] })}
          >
            Close
          </button>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title or author"
              onChange={this.bookSearch}
              value={this.state.query}
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            <BookHolder booksB={this.props.booksB} books={this.props.books} bookSearch={this.props.bookS}/>
          </ol>
        </div>
      </div>
    );
  }
}
