import React from "react";
import { Route } from "react-router-dom";
import * as BooksAPI from "./BooksAPI";
import BookShels from "./Components/Bookshels";
import "./App.css";
import SearchPage from "./Components/SearchPage";

class BooksApp extends React.Component {
  state = {
    books: [],
    booksB: [],
    error: "",
  };
  componentDidMount = async () => {
    const books = await BooksAPI.getAll();
    this.setState({ books });
  };

  bookChanger = (book, shelf) => {
    book.shelf = shelf;
    BooksAPI.update(book, shelf).catch((err) => {
      if (err) {
        this.setState({ error: err.message });
      }
    });
    this.setState((prev) => ({
      books: prev.books.filter((bb) => bb.id !== book.id).concat([book]),
    }));
  };
  bookSearch = async (query) => {
    const booksBack = await BooksAPI.search(query);
    booksBack = booksBack.map((bb) => {
      this.state.books.map((book) => {
        if (bb.id === book.id) {
          bb.shelf = book.shelf;
        }
        return bb;
      });
      // return book;
    });
  };
  render() {
    {
      this.state.error && console.log(this.state.error);
    }
    return (
      <div className="app">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <Route
          exact
          path="/"
          render={() => (
            <BookShels book={this.state.books} bookChanger={this.bookChanger} />
          )}
        />
        <Route
          exact
          path="/search"
          render={() => (
            <SearchPage
              books={this.state.books}
              bookChanger={this.bookChanger}
              bookSearch={this.bookSearch}
              bookB={this.state.booksB}
            />
          )}
        />
      </div>
    );
  }
}
export default BooksApp;
