import React, { Component } from "react";
import * as BooksAPI from "../BooksAPI";
import BookHolder from "./BookHolder";
export default class SearchPage extends Component {
  state = { booksB: [], query: "" };

  bookSearch = async (event) => {
    this.setState({ query: event.target.value });
    const booksBack = await BooksAPI.search(this.state.query);
    this.setState({ booksB: booksBack });
  };

  render() {
    console.log(this.state.booksB);
    return (
      <div className="search-books">
        <div className="search-books-bar">
          <button
            className="close-search"
            onClick={() => this.setState({ booksB: [] })}
          >
            Close
          </button>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title or author"
              onChange={this.bookSearch}
              value={this.state.query}
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            <BookHolder booksB={BookHolder}/>
          </ol>
        </div>
      </div>
    );
  }
}
